mapper.update:

update account
set nickname=#{nickname }
where no=${no }


update account
set password=#{password}
where no=${no }


update account
set name=#{name },
    phone_number=#{phoneNumber}
where no=#{no }


update account 
set token= #{token}
where no = #{no}


update account
    set password=#{randomPassword }
    where email=#{email }

mapper.select:

select no, email, nickname
  from account 
where email = #{email } 
  and password = #{password }


	select no, email, name, nickname, phone_number as phoneNumber, thumbnail_url as thumbnailUrl
 	 from account 
 	where no = #{no }


select email
	from account 
	where name = #{name} 
	and phone_number = #{phoneNumber}


select email, password
	from account 
	where name = #{name} 
	and email = #{email}
