mapper.select:

select no
 from  chat_room
 where no= #{no}
 and  password = #{password}

mapper.update:

update chat_room
set password= #{password }
where no = #{no }


update chat_room
set title= #{title }, limited_user_count= #{limitedUserCount }
, only_authorized= #{onlyAuthorized }, searchable = #{searchable} , thumbnail_url= #{thumbnailUrl }




, password = #{password}




where no = #{no }
