mapper.update:

update participant
set last_read_chat_no = #{lastReadChatNo }
where no = #{no}


update participant
set not_read_count = 0
where no = #{no}


update participant
set last_read_chat_no =  (select max(no) from message where chat_room_no = #{chatRoomNo })
where no = #{no}


update participant
set not_read_count = not_read_count +1
where chat_room_no= #{chatRoomNo }


update participant
set not_read_count = 0
where no= #{no }

mapper.select:

select ifnull(max(no), 0)
from message
where chat_room_no = #{chatRoomNo}

mapper.update:

update 
	participant
set
	favorite_room = #{favoriteRoom }
where 
	chat_room_no= #{chatRoomNo }
		and
	account_no = #{accountNo}
