mapper.update:

update participant
set last_read_chat_no = #{lastReadChatNo }
where no = #{no}


update participant
set not_read_count = 0
where no = #{no}


update participant
set last_read_chat_no =  (select max(no) from message where chatting_room_no = #{chattingRoomNo })
where no = #{no}


update participant
set not_read_count = not_read_count +1
where chatting_room_no= #{chattingRoomNo }


update participant
set not_read_count = 0
where no= #{no }
